import { HttpClientModule } from '@angular/common/http';
import { Component } from '@angular/core';
import { GenericControl, JsonFormService, NgxJsonFormComponent, convertArray, convertDependingArray } from '../../../ngx-json-form/src/public-api';
import { JsonPipe } from '@angular/common';
import { Observable, map, of, zip } from 'rxjs';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [
    HttpClientModule,
    NgxJsonFormComponent,
    JsonPipe
  ],
  templateUrl: './app.component.html',
  styleUrl: './app.component.sass'
})
export class AppComponent {
  formContent: GenericControl[]
  formStatus: any

  sign: string = 'data:image/png;base64,'
  // formValue: any = { id: '1', name: 'T', funktion: '2', funktion2: '3', active: true, dayOfBirth: '2021-01-01T00:00', description: '', signature: this.sign }
  // formValue: any = { dayOfBirth: '2021-01-01T00:00' }
  formValue: any = { id: '123', password: 'Abc123!', description: 'Beschreibung', active: true, toggle: true, zip: 12344 }

  options$: Observable<any> = of([
    { id: '1', value: 'Kategorie1' },
    { id: '2', value: 'Kategorie2' },
    { id: '3', value: 'Kategorie3' },
    { id: '4', value: 'Kategorie4' },
    { id: '5', value: 'Kategorie5' }
  ])
  depOptions$: Observable<any> = of([
    { id: '1', value: 'Bestandteil1', dep: 'Kategorie1' },
    { id: '2', value: 'Bestandteil2', dep: 'Kategorie1' },
    { id: '3', value: 'Bestandteil3', dep: 'Kategorie2' },
    { id: '4', value: 'Bestandteil4', dep: 'Kategorie3' },
    { id: '5', value: 'Bestandteil5', dep: 'Kategorie3' },
    { id: '6', value: 'Bestandteil6', dep: 'Kategorie4' },
    { id: '7', value: 'Bestandteil7', dep: 'Kategorie5' },
    { id: '8', value: 'Bestandteil8', dep: 'Kategorie5' },
    { id: '9', value: 'Bestandteil9', dep: 'Kategorie5' }
  ])

  files$: Observable<any> = of([
    "data:image/png;base64,"
  ])

  constructor(private _jsonFormService: JsonFormService) {
    this.files$.subscribe({
      next: (files) => {
        this._jsonFormService.setFiles(files)
      }
    })

    this.formContent = []

    // merge of two observables to fix depending properties
    zip(this.options$, this.depOptions$)
    .pipe(map(([options, depOptions]) => ({ options, depOptions })))
    .subscribe({
      next: (data) => {
        this.depOptions$ = of(
          data.depOptions.map((item: any) => ({
            ...data.options.find((t: any) => t.value === item.dep).id,
            ...item
          }))
        )
      }
    })

    zip(this.options$)
    .pipe(
      map(([options]) => ({ options }))
    )
    .subscribe({
      next: (data) => {
        this.formContent.push(
          {
            type: 'checkbox',
            defaultValue: false,
            disabled: false,
            hidden: false,
            key: 'active',
            label: 'Aktiv',
            value: false
          },
          {
            type: 'toggle',
            defaultValue: false,
            disabled: false,
            hidden: false,
            key: 'toggle',
            label: 'Toggle',
            value: false
          },
          {
            type: 'tag-input',
            disabled: false,
            hidden: false,
            key: 'tags',
            label: 'TagInput',
            tags: []
          },
          {
            type: 'input',
            class: '',
            defaultValue: 0,
            disabled: true,
            hidden: false,
            key: 'id',
            label: 'id',
            placeholder: '000'
          },
          {
            type: 'input',
            hidden: false,
            key: 'zip',
            label: 'ZIP',
            placeholder: '12345',
            validators: [
              { required: true },
              { zipCodeValidator: true }
            ],
            defaultValue: '12345'
          },
          {
            type: 'password',
            disabled: false,
            hidden: false,
            key: 'password',
            label: 'Passwort',
            placeholder: 'Passwort',
            show: false,
            validators: [
              { required: true },
              { validStrongPassword: true }
            ]
          },
          {
            type: 'textarea',
            defaultValue: '',
            key: 'description',
            label: 'Beschreibung',
            placeholder: 'Beschreibung zur Person',
            validators: [
              { required: true },
              { minlength: 10 },
              { maxlength: 100 }
            ]
          },
          {
            type: 'input',
            hidden: false,
            key: 'mask',
            label: 'Mask',
            mask: '00-000-00',
            placeholder: '12-345-67'
          },
          {
            type: 'select',
            disabled: false,
            hidden: false,
            key: 'funktion',
            label: 'Funktion',
            options: convertArray(data.options, 'value'),
            validators: [
              { required: true }
            ]
          },
          {
            type: 'input',
            class: '',
            defaultValue: 0,
            disabled: false,
            hidden: false,
            key: 'function_id',
            label: 'DependedInput',
            placeholder: '',
            dependOnKey: 'funktion',
            options: convertArray(data.options, 'value'),
          },
          {
            type: 'dependedselect',
            disabled: false,
            hidden: false,
            key: 'funktion2',
            label: 'DependedSelect',
            dependOnKey: 'funktion',
            options$:  convertDependingArray(this.depOptions$, 'value', '0'),
            validators: [
              { required: true }
            ]
          },
          {
            type: 'datetime-local',
            defaultValue: '',
            disabled: false,
            hidden: false,
            key: 'dayOfBirth',
            label: 'Geburtstag',
            placeholder: '',
            validators: [
              { required: true }
            ]
          },
          // {
          //   type: 'input',
          //   defaultValue: '',
          //   key: 'name',
          //   label: 'Name',
          //   placeholder: 'Thomas',
          //   validators: [
          //     { required: true }
          //   ],
          //   rules: [
          //     { property: 'compare', dependOn: { operation: 'eq', value: 'T', except: true }, value: false }
          //   ],
          // },

          {
            type: 'searchselect',
            disabled: false,
            hidden: false,
            key: 'searchfunktion',
            label: 'Funktion',
          },
          // {
          //   type: 'datetime-local',
          //   defaultValue: '',
          //   hidden: false,
          //   key: 'dayOfBirth2',
          //   label: '1. Geburtstag',
          //   placeholder: '',
          //   rules: [
          //     { property: 'compare', dependOn: { operation: 'gt', key: 'dayOfBirth' }, value: false },
          //     { property: 'compare', dependOn: { operation: 'eq', value: '0' }, value: false },
          //     { property: 'compare', dependOn: { operation: 'eq', key: 'dayOfBirth' }, value: false }
          //   ],
          //   validators: [
          //     { required: true }
          //   ]
          // },,
          {
            type: 'fileupload',
            disabled: false,
            hidden: false,
            key: 'file',
            label: 'Dateien hochladen',
            placeholder: 'Datei',
            multiple: true,
            upload: {
              url: 'https://v2.convertapi.com/upload',
              type: 'blob'
            }
          },
          {
            type: 'imageslider',
            disabled: false,
            files$: this.files$,
            hidden: false,
            key: 'images',
            label: 'Galerie'
          },
          {
            type: 'signature',
            disabled: false,
            hidden: false,
            key: 'signature',
            label: 'Unterschrift',
            description: 'Die sachliche, fachtechnische oder rechnerische Richtigkeit wird durch Unterzeichnung des Vermerks „Sachlich richtig“, „Fachtechnisch richtig“ oder „Rechnerisch richtig“ bescheinigt.',
            rules: [
              { property: 'hidden', dependOn: { key: 'funktion', operation: 'ne', except: '1' }, value: true }
            ],
            validators: [
              { required: true }
            ],
          },
          {
            type: 'pin',
            disabled: false,
            hidden: false,
            key: 'pin',
            label: 'Pin',
            placeholder: '1234'
          },
          {
            type: 'otp',
            disabled: false,
            hidden: false,
            key: 'otp',
            label: 'OTP',
            placeholder: '123456'
          }
          // {
          //   type: 'input',
          //   class: '',
          //   defaultValu e: 0,
          //   // disabled: true,
          //   hidden: false,
          //   key: 'test',
          //   label: 'Test',
          //   placeholder: '000'
          // }
          // {
          //   status: 'success',
          //   type: 'button',
          //   disabled: false,
          //   hidden: false,
          //   key: 'submit',
          //   label: 'Submit',
          // }
        )
      }
    })

    this._jsonFormService.setFormData(this.formValue)
    
    // this._jsonFormService.files$.subscribe({
    //   next: (files) => {
    //     console.log('files: ', files)
    //   }
    // })
  }

  valueChanges(formValue: any) {
    // get data back only if form is valid
    // console.log('valueChanges: ', formValue)
    this.formValue = formValue
  }

  statusChanges(formStatus: any) {
    // get status back only if form is valid
    // console.log('statusChanges: ', formStatus)
    this.formStatus = formStatus
  }
}

